// Extract matches array from webhook body
const webhookData = $input.first().json;
const matches = webhookData.body;

// Helper function to escape single quotes for SQL
function escapeSql(str) {
  if (!str) return '';
  return str.toString().replace(/'/g, "''");
}

// Return each match with SQL-safe values
return matches.map(match => ({
  json: {
    tournament: escapeSql(match.tournament),
    player1: escapeSql(match.player1),
    player2: escapeSql(match.player2),
    winner: escapeSql(match.winner),
    score: match.score,
    odds1: match.odds1,
    odds2: match.odds2,
    surface: match.surface,
    country: match.country,
    player1_nationality: match.nationality1,
    player2_nationality: match.nationality2,
    match_date: match.match_date || new Date().toISOString().split('T')[0]
  }
}));

// Get all updated predictions from the loop
const results = $("Loop Over Items").all();

// Calculate statistics
const total = results.length;
const correct = results.filter(r => r.json.prediction_correct === true).length;
const incorrect = total - correct;
const accuracy = ((correct / total) * 100).toFixed(2);

// Calculate average confidence
const avgConfidence = Math.round(
  results.reduce((sum, r) => sum + r.json.confidence_score, 0) / total
);

// Group by confidence bucket
const highConf = results.filter(r => r.json.confidence_score >= 60);
const mediumConf = results.filter(r => r.json.confidence_score >= 50 && r.json.confidence_score < 60);
const lowConf = results.filter(r => r.json.confidence_score < 50);

const stats = {
  date: new Date().toISOString().split('T')[0],
  total_predictions: total,
  correct_predictions: correct,
  incorrect_predictions: incorrect,
  accuracy_percentage: parseFloat(accuracy),
  average_confidence: avgConfidence,

  high_confidence: {
    total: highConf.length,
    correct: highConf.filter(r => r.json.prediction_correct).length,
    accuracy: highConf.length > 0 ?
      ((highConf.filter(r => r.json.prediction_correct).length / highConf.length) * 100).toFixed(2) : 0
  },

  medium_confidence: {
    total: mediumConf.length,
    correct: mediumConf.filter(r => r.json.prediction_correct).length,
    accuracy: mediumConf.length > 0 ?
      ((mediumConf.filter(r => r.json.prediction_correct).length / mediumConf.length) * 100).toFixed(2) : 0
  },

  low_confidence: {
    total: lowConf.length,
    correct: lowConf.filter(r => r.json.prediction_correct).length,
    accuracy: lowConf.length > 0 ?
      ((lowConf.filter(r => r.json.prediction_correct).length / lowConf.length) * 100).toFixed(2) : 0
  }
};

return { json: stats };

