const response = $input.first().json;

// Extract JSON from OpenAI node response
let analysis;
try {
  // OpenAI node returns the message content directly
  let content = response.message?.content || response.choices?.[0]?.message?.content;

  // Check if the content is a string and if it contains a JSON markdown block
  if (typeof content === 'string') {
    // Regular expression to find and extract the content of a JSON code block.
    // This handles potential leading/trailing whitespace and newlines around the JSON.
    const jsonRegex = /```json\n([\s\S]*?)\n```/;
    const match = content.match(jsonRegex);

    // If a match is found, use the captured JSON string.
    if (match && match[1]) {
      content = match[1];
    }
  }

  // Try to parse the (potentially cleaned) content as JSON
  analysis = typeof content === 'string' ? JSON.parse(content) : content;

} catch (e) {
  console.error('Failed to parse LLM response:', e);
  analysis = {
    summary: 'LLM analysis failed to parse',
    key_failures: [],
    patterns_discovered: [],
    confidence_calibration: {
      high_confidence_analysis: 'N/A',
      recommended_adjustment: 'N/A'
    },
    action_items: ['Review LLM response format']
  };
}

return { json: analysis };

const analysis = $input.first().json;
const dailyStats = $('Calculate Daily Stats').first().json;

// Helper function to escape single quotes for SQL
function escapeSql(str) {
  if (!str) return '';
  return str.toString().replace(/'/g, "''");
}

// Build learning log entries from LLM analysis
const learningEntries = [];

// 1. Daily summary entry
learningEntries.push({
  json: {
    log_type: 'daily_summary',
    description: escapeSql(analysis.summary),
    learning_data: JSON.stringify({
      daily_stats: dailyStats,
      total_failures: analysis.key_failures.length,
      patterns_found: analysis.patterns_discovered.length
    }).replace(/'/g, "''"),  // Escape quotes in JSON too
    impact_score: 5
  }
});

// 2. Individual failure entries
analysis.key_failures.forEach(failure => {
  learningEntries.push({
    json: {
      log_type: 'prediction_failure',
      description: escapeSql(failure.failure_reason),
      related_prediction_id: failure.prediction_id,
      learning_data: JSON.stringify({
        learning: failure.learning,
        impact_score: failure.impact_score
      }).replace(/'/g, "''"),
      impact_score: failure.impact_score || 5
    }
  });
});

// 3. Pattern discovery entries
analysis.patterns_discovered.forEach(pattern => {
  learningEntries.push({
    json: {
      log_type: 'pattern_discovery',
      description: escapeSql(pattern.description),
      learning_data: JSON.stringify({
        pattern_type: pattern.pattern_type,
        affected_predictions: pattern.affected_predictions,
        recommendation: pattern.recommendation
      }).replace(/'/g, "''"),
      impact_score: 7
    }
  });
});

// 4. Confidence calibration entry
learningEntries.push({
  json: {
    log_type: 'confidence_calibration',
    description: escapeSql(analysis.confidence_calibration.high_confidence_analysis),
    learning_data: JSON.stringify({
      recommended_adjustment: analysis.confidence_calibration.recommended_adjustment,
      daily_accuracy: dailyStats.accuracy_percentage
    }).replace(/'/g, "''"),
    impact_score: 6
  }
});

return learningEntries;

