const analysis = $('Extract LLM Response').first().json;

// Helper function to escape single quotes for SQL
function escapeSql(str) {
  if (!str) return '';
  return str.toString().replace(/'/g, "''");
}

// Extract player-specific insights from LLM response
const playerInsights = [];

if (analysis.player_specific_insights && analysis.player_specific_insights.length > 0) {
  analysis.player_specific_insights.forEach(insight => {
    // Build supporting data object
    const supportingData = insight.supporting_data || {};

    playerInsights.push({
      json: {
        player_name: escapeSql(insight.player_name),
        insight_type: insight.insight_type,
        insight_description: escapeSql(insight.description),
        confidence: insight.confidence || 60,
        supporting_data: JSON.stringify({
          affected_predictions: insight.affected_predictions,
          recommendation: insight.recommendation,
          ...supportingData  // Include score, odds, etc.
        }).replace(/'/g, "''"),
        discovered_date: new Date().toISOString().split('T')[0]
      }
    });
  });
}

// Return insights or skip marker if none found
return playerInsights.length > 0 ? playerInsights : [{ json: { skip: true } }];
