const llmResponse = $input.item.json;
const matchContext = $("Build Match Context").item.json;

// Generate match_id from match details
// Use match_date from context (which has fallback to current date)
const matchDate = matchContext.match_date;
const matchId = `${matchContext.tournament}_${cleanPlayerName(matchContext.player1.name)}_${cleanPlayerName(matchContext.player2.name)}_${matchDate}`.replace(/[^a-zA-Z0-9_]/g, '_');

// Extract and parse prediction
let prediction = llmResponse.message.content;

// If content is a string (wrapped in markdown), extract and parse JSON
if (typeof prediction === 'string') {
  let jsonString = prediction.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  jsonString = jsonString.replace(/}\s*[\s\S]*$/, '}').trim();
  // Clean up the JSON string before parsing
  jsonString = cleanJsonString(jsonString);

  try {
    prediction = JSON.parse(jsonString);
  } catch (error) {
    throw new Error(`Failed to parse LLM JSON for match ${matchId}:\nError: ${error.message}\n\nRaw JSON string:\n${jsonString}`);
  }
}

// Helper function to clean JSON string before parsing
function cleanJsonString(jsonString) {
  if (!jsonString) return jsonString;

  return jsonString
    .replace(/\\n/g, ' ')          // Replace escaped newlines with spaces
    .replace(/\\t/g, ' ')           // Replace escaped tabs with spaces
    .replace(/\\r/g, ' ')           // Replace escaped carriage returns
    .replace(/\\\\/g, '\\')         // Fix double backslashes
    .replace(/\\"/g, '"')           // Fix escaped quotes
    .replace(/\\'/g, "'")           // Fix escaped single quotes
    .replace(/\\b/g, '')            // Remove backspace
    .replace(/\\f/g, '')            // Remove form feed
    .replace(/\\v/g, '')            // Remove vertical tab
    .replace(/\\ /g, ' ')           // Fix forward slashes
    .replace(/[\x00-\x1F\x7F]/g, '') // Remove control characters
    .trim();
}

// Helper function to escape single quotes for SQL
function escapeSql(str) {
  if (!str) return '';
  return str.toString().replace(/'/g, "''");
}

// Helper function to clean player names (remove nationality brackets)
function cleanPlayerName(name) {
  if (!name) return '';
  return name.replace(/\s*\([^)]*\)/g, '').trim();
}

// Cap confidence based on max_confidence from system
const maxConfidence = matchContext.system.max_confidence / 100;
if (prediction.confidence_score > maxConfidence) {
  prediction.confidence_score = maxConfidence;
  prediction.reasoning += ` [Confidence capped at ${matchContext.system.max_confidence}% due to ${matchContext.system.learning_phase}]`;
}

// Determine data limitations based on learning phase
let dataLimitations = '';
if (matchContext.system.days_operated < 7) {
  dataLimitations = 'Phase 1: Limited historical data, relying primarily on odds and available player stats';
} else if (matchContext.system.days_operated < 21) {
  dataLimitations = 'Phase 2: Emerging patterns, moderate historical data available';
} else {
  dataLimitations = 'Phase 3: Mature system with comprehensive historical data';
}

// Check if we have surface-specific data for either player
const surfaceDataAvailable = (matchContext.player1.has_stats && matchContext.player1.stats.win_rate_surface > 0) ||
                               (matchContext.player2.has_stats && matchContext.player2.stats.win_rate_surface > 0);

const output = {
  match_id: escapeSql(matchId),
  tournament: escapeSql(matchContext.tournament),
  surface: escapeSql(matchContext.surface),
  player_1: escapeSql(cleanPlayerName(matchContext.player1.name)),
  player_2: escapeSql(cleanPlayerName(matchContext.player2.name)),
  odds_player_1: matchContext.player1.odds,
  odds_player_2: matchContext.player2.odds,
  predicted_winner: escapeSql(cleanPlayerName(prediction.predicted_winner)),
  confidence_score: Math.round(prediction.confidence_score * 100),
  reasoning: escapeSql(prediction.reasoning),
  risk_assessment: escapeSql(prediction.risk_assessment),
  value_bet: prediction.value_bet,
  recommended_action: escapeSql(prediction.recommended_action),
  prediction_date: matchDate,
  data_quality_at_prediction: matchContext.system.data_quality_score,
  learning_phase_at_prediction: escapeSql(matchContext.system.learning_phase),

  // Additional context fields
  days_operated: matchContext.system.days_operated,
  system_accuracy_at_prediction: matchContext.system.overall_accuracy,
  data_limitations: escapeSql(dataLimitations),
  player1_data_available: matchContext.player1.has_stats,
  player2_data_available: matchContext.player2.has_stats,
  h2h_data_available: false, // Not implemented yet
  surface_data_available: surfaceDataAvailable,
  similar_matches_count: matchContext.similar_matches.length
};

return { json: output };

// Get current match data from the loop
const matchItem = $('Loop Over Items').item.json;

// Extract match_date from incoming data (with fallback to current date)
const matchDate = matchItem.match_date || new Date().toISOString().split('T')[0];

// Get system metadata
const systemItems = $('System Health Check').all();
const systemData = systemItems.length > 0 ? systemItems[0].json : {};

// Access Postgres node results as items
const player1Items = $('Query Player 1 Stats').all();
const player1Stats = player1Items.length > 0 ? player1Items[0].json : null;

const player2Items = $('Query Player 2 Stats').all();
const player2Stats = player2Items.length > 0 ? player2Items[0].json : null;

// Get player insights for both players
const playerInsightsItems = $('Query Player Insights').all();
const player1Insights = playerInsightsItems.filter(item =>
  item.json.player_name === matchItem.player1
);
const player2Insights = playerInsightsItems.filter(item =>
  item.json.player_name === matchItem.player2
);

// Build insights summary
const buildInsightsSummary = (insights) => {
  if (insights.length === 0) return null;

  return insights.map(insight => ({
    type: insight.json.insight_type,
    description: insight.json.insight_description,
    confidence: insight.json.confidence,
    days_ago: Math.floor((new Date().getTime() - new Date(insight.json.discovered_date).getTime()) / (1000 * 60 * 60 * 24)),
    supporting_data: insight.json.supporting_data  // â† REMOVED JSON.parse()
  }));
};


// Get filtered learning insights from Summarize LLM node (runs once for all matches)
let learningInsights = {
  has_insights: false,
  relevant_patterns: [],
  signal_boosts: [],
  confidence_adjustments: []
};
try {
  const summaryItem = $('Summarize Learning Insights').first();
  if (summaryItem && summaryItem.json) {
    const summary = summaryItem.json;

    // Parse LLM response
    let parsedInsights = summary.message?.content || summary;
    if (typeof parsedInsights === 'string') {
      try {
        parsedInsights = JSON.parse(parsedInsights.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim());
      } catch (e) {
        parsedInsights = { relevant_patterns: [], signal_boosts: [], confidence_adjustments: [] };
      }
    }

    // Check if we have any insights
    if (parsedInsights.relevant_patterns?.length > 0 ||
        parsedInsights.signal_boosts?.length > 0 ||
        parsedInsights.confidence_adjustments?.length > 0) {
      learningInsights.has_insights = true;
      learningInsights.relevant_patterns = parsedInsights.relevant_patterns || [];
      learningInsights.signal_boosts = parsedInsights.signal_boosts || [];
      learningInsights.confidence_adjustments = parsedInsights.confidence_adjustments || [];
    }
  }
} catch (e) {
  learningInsights.has_insights = false;
}

// Extract signal boosting weights from filtered signal_boosts
let signalBoosts = {
  giant_killer_multiplier: 1.0,
  surface_specialist_multiplier: 1.0,
  recent_form_multiplier: 1.0,
  vs_favorites_multiplier: 1.0
};

// Parse signal_boosts from LLM filtering
if (learningInsights.has_insights && learningInsights.signal_boosts.length > 0) {
  learningInsights.signal_boosts.forEach(boost => {
    const boostLower = boost.toLowerCase();
    if (boostLower.includes('giant') && boostLower.includes('killer')) {
      signalBoosts.giant_killer_multiplier = 1.5;
    }
    if (boostLower.includes('surface') && (boostLower.includes('specialist') || boostLower.includes('specific'))) {
      signalBoosts.surface_specialist_multiplier = 1.3;
    }
    if (boostLower.includes('momentum') || boostLower.includes('form') || boostLower.includes('recent')) {
      signalBoosts.recent_form_multiplier = 1.2;
    }
    if (boostLower.includes('favorite') && boostLower.includes('beat')) {
      signalBoosts.vs_favorites_multiplier = 1.4;
    }
  });
}

// Get Pinecone similar matches
let pineconeMatches = [];
try {
  const pineconeResponse = $('Query Pinecone Similar Matches').first().json;
  if (pineconeResponse && pineconeResponse.result && pineconeResponse.result.hits) {
    pineconeMatches = pineconeResponse.result.hits;
  }
} catch (e) {
  pineconeMatches = [];
}

// Build similar matches context
const similarMatches = [];
if (pineconeMatches.length > 0) {
  for (let i = 0; i < Math.min(3, pineconeMatches.length); i++) {
    const hit = pineconeMatches[i];
    if (hit.fields) {
      similarMatches.push({
        similarity_score: (hit._score * 100).toFixed(1) + '%',
        tournament: hit.fields.tournament || 'Unknown',
        surface: hit.fields.surface || 'Unknown',
        date: hit.fields.date || 'Unknown',
        odds: `${hit.fields.odds1 || '?'} / ${hit.fields.odds2 || '?'}`,
        score: hit.fields.score || 'Unknown',
        was_upset: hit.fields.is_upset || false,
        odds_range: hit.fields.odds_range || 'Unknown',
        match_summary: hit.fields.match_context || 'No context available'
      });
    }
  }
}

// Calculate odds analysis
const odds1 = parseFloat(matchItem.odds1);
const odds2 = parseFloat(matchItem.odds2);
const player1ImpliedProb = ((1 / odds1) * 100).toFixed(2);
const player2ImpliedProb = ((1 / odds2) * 100).toFixed(2);
const favorite = odds1 < odds2 ? matchItem.player1 : matchItem.player2;

// Calculate max confidence based on learning phase
const daysOperated = systemData.days_operated || 0;
const maxConfidence = daysOperated < 7 ? 60 : daysOperated < 21 ? 75 : 100;

// Check if players have useful stats (more than 0 matches)
const player1HasStats = player1Stats && player1Stats.total_matches > 0;
const player2HasStats = player2Stats && player2Stats.total_matches > 0;

// Calculate dynamic data quality score
let dataQualityScore = 50; // Base score
if (player1HasStats) dataQualityScore += 25;
if (player2HasStats) dataQualityScore += 25;
if (similarMatches.length > 0) dataQualityScore += 20;

// Build complete context in the format expected by the prompt
return {
  json: {
    // Match details
    tournament: matchItem.tournament,
    surface: matchItem.surface,
    match_date: matchDate,

    // Player 1 (with signal boosts applied)
    player1: {
      name: matchItem.player1,
      nationality: matchItem.nationality1 || 'Unknown',
      odds: odds1,
      has_stats: player1HasStats,
      stats: player1HasStats ? {
        total_matches: player1Stats.total_matches,
        win_rate_overall: player1Stats.win_rate_overall,
        win_rate_surface: Math.min(100, (player1Stats[`win_rate_${matchItem.surface.toLowerCase()}`] || 0) * signalBoosts.surface_specialist_multiplier),
        recent_form: player1Stats.recent_form_last_5,
        momentum_score: Math.min(10, player1Stats.momentum_score * signalBoosts.recent_form_multiplier),
        upset_wins: player1Stats.upset_wins_count,
        giant_killer_score: Math.min(10, player1Stats.giant_killer_score * signalBoosts.giant_killer_multiplier),
        avg_odds_winning: player1Stats.avg_odds_when_winning,
        vs_favorites_rate: Math.min(100, player1Stats.vs_favorites_win_rate * signalBoosts.vs_favorites_multiplier),
        last_match: player1Stats.last_match_date
      } : null,
      insights: buildInsightsSummary(player1Insights),
      has_insights: player1Insights.length > 0
    },

    // Player 2 (with signal boosts applied)
    player2: {
      name: matchItem.player2,
      nationality: matchItem.nationality2 || 'Unknown',
      odds: odds2,
      has_stats: player2HasStats,
      stats: player2HasStats ? {
        total_matches: player2Stats.total_matches,
        win_rate_overall: player2Stats.win_rate_overall,
        win_rate_surface: Math.min(100, (player2Stats[`win_rate_${matchItem.surface.toLowerCase()}`] || 0) * signalBoosts.surface_specialist_multiplier),
        recent_form: player2Stats.recent_form_last_5,
        momentum_score: Math.min(10, player2Stats.momentum_score * signalBoosts.recent_form_multiplier),
        upset_wins: player2Stats.upset_wins_count,
        giant_killer_score: Math.min(10, player2Stats.giant_killer_score * signalBoosts.giant_killer_multiplier),
        avg_odds_winning: player2Stats.avg_odds_when_winning,
        vs_favorites_rate: Math.min(100, player2Stats.vs_favorites_win_rate * signalBoosts.vs_favorites_multiplier),
        last_match: player2Stats.last_match_date
      } : null,
      insights: buildInsightsSummary(player2Insights),
      has_insights: player2Insights.length > 0
    },

    // Odds analysis
    odds_analysis: {
      player1_implied_prob: player1ImpliedProb,
      player2_implied_prob: player2ImpliedProb,
      favorite: favorite
    },

    // Similar matches
    similar_matches: similarMatches,
    has_similar_matches: similarMatches.length > 0,

    // Learning insights from LLM filtering
    learning_insights: learningInsights,

    // System context
    system: {
      days_operated: daysOperated,
      learning_phase: systemData.learning_phase || 'phase1_data_collection',
      overall_accuracy: systemData.overall_accuracy || 0,
      data_quality_score: dataQualityScore,
      max_confidence: maxConfidence
    }
  }
};

// Get all stored predictions from the loop
const predictions = $input.all();

// Get system health data
