// Helper function to clean player names
function cleanPlayerName(name) {
  if (!name) return '';
  return name.replace(/\s*\([^)]*\)/g, '').trim();
}

// Extract matches from body and add match_id to each match
 const matches = $input.all().map(item => item.json.body).flat();

 // Add match_id to each match
 const matchesWithIds = matches.map(match => {
   const matchDate = match.match_date || new Date().toISOString().split('T')[0];
   const matchId = `${match.tournament}_${cleanPlayerName(match.player1)}_${cleanPlayerName(match.player2)}_${matchDate}`
     .replace(/[^a-zA-Z0-9_]/g, '_');

   return {
     ...match,
     match_id: matchId
   };
 });

return matchesWithIds;
